import { Alert, Button, Card, Form, Input, message, PageHeader, Upload } from "antd";
import * as React from "react";
import { InboxOutlined } from '@ant-design/icons';
import { info } from "console";

const {Dragger} = Upload

const Encrypt = () => {

    const props = {
        name: 'file',
        multiple: false,
        previewVisible: true,
        onChange(info) {
            const {status} = info.file;
            if(status !== 'uploading') {
                console.log(info.file, info.fileList);
            }
            if(status === 'done')
            {
                message.success('${info.file.name} file upload failed.');
            }
        },
        onDrop(e) {
            console.log('Dropped files', e.dataTransfer.files);
        },
    };

    return (
        <>
            <PageHeader title="Encrypt" className="main-content">
                <Card>
                    <Form>
                        <Dragger {...props}>
                            <p className="ant-upload-drag-icon">
                                <InboxOutlined />
                            </p>
                            <p className="ant-upload-text">
                                Click or drag file to this area to upload.
                            </p>
                            <p className="ant-upload-hint">
                                Support for a single upload. Supported files include .png, .jpg, or .pdf
                            </p>
                        </Dragger>,
                        mountNode,
                        <Form.Item
                            label="Message"
                            name="message"
                        >
                            <Input />
                        </Form.Item>
                        <Button type="primary">Encrypt</Button>
                    </Form>
                </Card>
            </PageHeader>
        </>
    );
};

export default Encrypt;
